krpano.action:

calc(local.xframes, (caller.imagewidth /framewidth) BOR 0); calc(local.frames, xframes * ((caller.imageheight / frameheight) BOR 0)); def(local.frame, integer, 0);

calc(caller.crop, '0|0|' + framewidth + '|' + frameheight);

setinterval(calc('crop_anim_' + caller.name), calc(5.0 / framerate), if(caller.loaded, inc(frame); if(frame GE frames, if(caller.onlastframe !== null, callwith(caller, onlastframe() ) ); set(frame,0); ); mod(xpos, frame, xframes); div(ypos, frame, xframes); Math.floor(ypos); mul(xpos, framewidth); mul(ypos, frameheight); calc(caller.crop, xpos + '|' + ypos + '|' + framewidth + '|' + frameheight); ,

clearinterval(calc('crop_anim_' + caller.name)); ); );

if(device.mobile OR device.tablet OR device.iphone,
showtext(ATENCIÓN:La aplicación solo funciona bien en modo escritorio.[br]No está diseñada para dispositivos táctiles ni de pantalla pequeña,movil), 
);

